void main(List<String> args) => runApp(MyApp());

class MyApp extends StatefulWidget {
  @override
  State<StatefulWidget> createState() {
    return _MyAppState();
  }
}

class _MyAppState extends State<MyApp> {
  final _questions = const [
    {
      'questionText': 'who is current president of india ? ',
      'answers': [
        {'text': 'Droupadi Murmu', 'score': 10},
        {'text': 'Narendra Modi', 'score': 0},
        {'text': 'pranab mukherji', 'score': 0},
        {'text': 'Ramnath Kovind', 'score': 0},
      ],
    },
    {
      'questionText': 'who is current ceo of amazon ?',
      'answers': [
        {'text': 'Jeff Bezos', 'score': 0},
        {'text': 'Sundar Pichai', 'score': 0},
        {'text': 'Andy Jassy', 'score': 10},
        {'text': 'Bill Gates', 'score': 0},
      ],
    },
    {
      'questionText': 'who is known as father of mathematics ? ',
      'answers': [
        {'text': 'Aryabhatta', 'score': 0},
        {'text': 'Ramanujan', 'score': 0},
        {'text': 'Archmedies', 'score': 10},
        {'text': 'Isaac Newton', 'score': 0},
      ],
    },
    {
      'questionText': 'which college is known as chor college  ? ',
      'answers': [
        {'text': 'CGPIT', 'score': 0},
        {'text': 'BMU', 'score': 10},
        {'text': 'CKPT', 'score': 0},
        {'text': 'SCET', 'score': 0},
      ],
    },
    {
      'questionText': 'what is real name of Marvel black widow ? ',
      'answers': [
        {'text': 'Brie Larson', 'score': 0},
        {'text': 'Elizabeth Olsen', 'score': 0},
        {'text': 'Gemma Chan', 'score': 0},
        {'text': ' Natasha Romanoff', 'score': 10},
      ],
    },
  ];
  var _questionIndex = 0;
  var _totalScore = 0;
  void _resetQuiz() {
    setState(() {
      _questionIndex = 0;
      _totalScore = 0;
    });
  }

  void _answerQuestion(int score) {
    _totalScore = _totalScore + score;
    setState(() {
      _questionIndex = _questionIndex + 1;
    });
    print(_questionIndex);
    if (_questionIndex < _questions.length) {
      print('we have more questions');
    } else {
      print("no more questions.");
    }
  }

  @override
  Widget build(BuildContext context) {
    // ignore: todo
    // TODO: implement build
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: Text('my app'),
        ),
        body: _questionIndex < _questions.length
            ? Quiz(
                answerQuestion: _answerQuestion,
                questionIndex: _questionIndex,
                questions: _questions,
              )
            : Result(_totalScore, _resetQuiz),
      ),
    );
  }
}
